$(document).ready(function(){$("#searchUser").on("keyup",function(e){let t=e.target.value;$.ajax({url:"https://api.github.com/users/"+t,data:{client_id:"d9308aacf8b204d361fd",client_secret:"62551cc02cee983fff0bac41baf170eb5a312c1c"}}).done(function(e){$.ajax({url:"https://api.github.com/users/"+t+"/repos",data:{client_id:"",client_secret:"",sort:"created: asc",per_page:5}}).done(function(e){$.each(e,function(e,t){$("#repos").append(`\r\n              <div class="ui card">\r\n                <div class="header">\r\n                  <strong>${t.name}</strong>:</div>\r\n                  <div class="description"> ${t.description}</div>\r\n                  <div class="extra content">\r\n                  <i class="heart icon"></i><span class="label">Forks: ${t.forks_count}</span>\r\n                  <i class="user icon"></i><span class="label">Watchers: ${t.watchers_count}</span>\r\n                  <i class="star icon"></i><span class="label">Stars: ${t.stargazers_count}</span>\r\n                </div>\r\n                </div>\r\n                  <a href="${t.html_url}" target="_blank" class="ui button">Repo Page</a>\r\n              </div>\r\n          `)})}),$("#profile").html(`\r\n        <div class="panel">\r\n          <h3 class="panel-title">${e.name}</h3>\r\n          <div class="panel-body">\r\n            <div class="row">\r\n                <img class="ui image" src="${e.avatar_url}">\r\n                <br />\r\n                <a target="_blank" class="button" href="${e.html_url}">View Profile</a>\r\n              </div>\r\n              <span class="ui label">Public Repos: ${e.public_repos}</span>\r\n              <span class="ui label">Public Gists: ${e.public_gists}</span>\r\n              <span class="ui label">Followers: ${e.followers}</span>\r\n              <span class="ui label">Following: ${e.following}</span>\r\n              <br><br>\r\n              <ul class="list-group">\r\n                <li class="item">Company: ${e.company}</li>\r\n                <li class="item">Website/blog: ${e.blog}</li>\r\n                <li class="item">Location: ${e.location}</li>\r\n                <li class="item">Member Since: ${e.created_at}</li>\r\n              </ul>\r\n              </div>\r\n            </div>\r\n          </div>\r\n        </div>\r\n        <h3>Latest Repos</h3>\r\n        <div id="repos"></div>\r\n      `)})})});